func toggleDescription(id int64) templ.ComponentScript {
	return templ.ComponentScript{
		Name: `__templ_toggleDescription_2393`,
		Function: `function __templ_toggleDescription_2393(id){const desc = document.getElementById("task-" + id + "__description");
    const icon = document.getElementById("task-" + id + "__expand-icon");
    const isHidden = desc.classList.contains("hidden");

    if (isHidden) {
        mod.show(desc.id);
        icon.innerHTML = "expand_less"
    } else {
        mod.hide(desc.id);
        icon.innerHTML = "expand_more"
    }}`,
		Call:       templ.SafeScript(`__templ_toggleDescription_2393`, id),
		CallInline: templ.SafeScriptInline(`__templ_toggleDescription_2393`, id),
	}
}

